// Base64 encoded short sound effects
const SOUNDS = {
  click: 'data:audio/mp3;base64,',
  success: 'data:audio/mp3;base64,',
  error: 'data:audio/mp3;base64,',
};

class SoundManager {
  constructor() {
    this.enabled = true;
    this.audioElements = {};
    
    // Pre-load audio elements
    Object.entries(SOUNDS).forEach(([key, value]) => {
      const audio = new Audio(value);
      audio.volume = 0.3;
      this.audioElements[key] = audio;
    });
  }

  setEnabled(enabled) {
    this.enabled = enabled;
  }

  play(sound) {
    if (!this.enabled) return;
    
    const audio = this.audioElements[sound];
    if (audio) {
      audio.currentTime = 0;
      audio.play().catch(() => {}); // Ignore autoplay restrictions
    }
  }
}

export const soundManager = new SoundManager(); 